sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var whatneeded = g_form.getValue('u_choice_4');

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('u_choice_4');

		// If the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'Choice 1'){
			g_form.addOption('u_choice_4','Choice 1','hr1');
			g_form.addOption('u_choice_4','Choice 2','hr2');
			g_form.addOption('u_choice_4','other','Other');
		}
		// If the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
		if(newValue == 'Choice 2'){
			g_form.addOption('u_choice_4','Choice 1','facilities1');
			g_form.addOption('u_choice_4','Choice 2','facilities2');
			g_form.addOption('u_choice_4','other','Other');
		}
		// If the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		if(newValue == 'Choice 3'){
			g_form.addOption('u_choice_4','Choice 1','legal1');
			g_form.addOption('u_choice_4','Choice 2','legal2');
			g_form.addOption('u_choice_4','other','Other');
		}		
		if(newValue == 'Choice 4'){
			g_form.addOption('u_choice_4','Choice 1','kor1');
			g_form.addOption('u_choice_4','Choice 2','kor2');
			g_form.addOption('u_choice_4','other','Other');
		}

		// If the form is loading and it is not a new record, set the u_choice_4 value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_choice_4', 'what is needed');
		}
	}
